func processSantaPath(char: Character) -> Int {
    if (char == "(") {
        return 1
    } else if (char == ")") {
        return -1
    } else {
        return 0
    }
}

var currentInput = 0
var reachesBasmentAtInput = Int.max
let result = input.characters.reduce(0) { (total, char) -> Int in
    //increment total
    currentInput += 1
    let nextFlor = total + processSantaPath(char)
    
    //check when reaches basmenet
    if (nextFlor == -1) {
        reachesBasmentAtInput = min(reachesBasmentAtInput, currentInput)
    }
    
    return nextFlor
}

print("Santa is on \(result)th flor")
print("Santa reaches basement at \(reachesBasmentAtInput)th ride")
